import React from 'react';

const Medya = ({ songs }) => (
  <div className="min-h-screen flex flex-col items-center justify-start px-2 md:px-0" style={{ background: 'repeating-linear-gradient(0deg, #2d1a1a 0px, #2d1a1a 1px, transparent 1px, transparent 32px), repeating-linear-gradient(90deg, #2d1a1a 0px, #2d1a1a 1px, transparent 1px, transparent 32px)', backgroundColor: '#1a0d0d', minHeight: '100vh' }}>
    <div className="w-full max-w-2xl bg-black/80 rounded-2xl p-4 sm:p-8 shadow-2xl border-4 border-theme mb-12 flex flex-col items-center justify-center text-center" style={{ marginTop: '7rem' }}>
      <h2 className="text-3xl sm:text-4xl font-extrabold text-theme mb-4" style={{ fontFamily: 'cursive', textShadow: '2px 2px 8px #000' }}>Medya / MP3</h2>
      {songs.length === 0 ? (
        <div className="text-center py-12">
          <div className="text-theme text-lg">Henüz şarkı yok</div>
        </div>
      ) : (
        <ul className="space-y-2">
          {songs.map((song, i) => (
            <li key={song.id} className="flex flex-col sm:flex-row items-start sm:items-center justify-between px-2 sm:px-4 py-2 rounded-lg hover:bg-theme/40 transition-all">
              <span className="text-theme font-semibold text-base sm:text-lg" style={{ fontFamily: 'cursive' }}>{String(i+1).padStart(2, '0')}. {song.title} <span className="text-theme">{song.artist && `- ${song.artist}`}</span></span>
            </li>
          ))}
        </ul>
      )}
    </div>
  </div>
);

export default Medya;
