import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const menuItems = [
  { label: 'ANASAYFA', path: '/' },
  { label: 'HAKKIMDA', path: '/hakkimda' },
  { label: 'MEDYA - MP3', path: '/medya' },
  { label: 'Z. DEFTERİ', path: '/zdefteri' },
  { label: 'İLETİŞİM', path: '/iletisim' },
];

const Navbar = () => {
  const location = useLocation();
  return (
    <>
      {/* Üstte sabit başlık */}
      <header className="fixed top-0 left-0 w-full z-30 bg-black/80 border-b-4 border-yellow-900 shadow-xl">
        <div className="text-5xl font-extrabold text-yellow-300 text-center py-4 tracking-widest" style={{ fontFamily: 'cursive', textShadow: '4px 4px 16px #000' }}>
          YENTİ RAP
        </div>
      </header>
      {/* Sağda menü */}
      <aside className="fixed right-0 top-32 z-20 flex flex-col items-end pr-6 space-y-4 select-none">
        {menuItems.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className={`w-48 text-right text-lg font-bold tracking-widest py-3 px-6 rounded-l-2xl border-4 border-yellow-700 shadow-lg bg-gradient-to-r from-yellow-900 via-yellow-800 to-yellow-700 text-yellow-200 hover:bg-yellow-600 hover:text-white transition-all duration-200 ${location.pathname === item.path ? 'bg-yellow-700 text-white border-yellow-400' : ''}`}
            style={{ fontFamily: 'cursive', letterSpacing: '2px', textShadow: '2px 2px 8px #000' }}
          >
            {item.label}
          </Link>
        ))}
      </aside>
    </>
  );
};

export default Navbar;
