import React from 'react';
import { Play, Heart, Eye } from 'lucide-react';

const Home = ({ songs, content, onPlaySong, currentSong, isPlaying }) => {
  return (
    <div className="space-y-8">
      {/* Hero Section */}
      <div className="text-center space-y-4">
        <h1 className="text-4xl md:text-6xl font-bold text-white">
          {content.title || 'Müzik Paylaşım Sitesi'}
        </h1>
        <p className="text-xl text-white/80 max-w-2xl mx-auto">
          {content.description || 'Şarkılarınızı paylaşın ve dinleyin'}
        </p>
      </div>

      {/* İstatistikler */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="bg-white/10 backdrop-blur-md rounded-lg p-6 text-center">
          <div className="text-3xl font-bold text-white">{songs.length}</div>
          <div className="text-white/60">Toplam Şarkı</div>
        </div>
        <div className="bg-white/10 backdrop-blur-md rounded-lg p-6 text-center">
          <div className="text-3xl font-bold text-white">
            {songs.reduce((total, song) => total + (song.plays || 0), 0)}
          </div>
          <div className="text-white/60">Toplam Dinleme</div>
        </div>
        <div className="bg-white/10 backdrop-blur-md rounded-lg p-6 text-center">
          <div className="text-3xl font-bold text-white">
            {songs.length > 0 ? Math.round(songs.reduce((total, song) => total + (song.plays || 0), 0) / songs.length) : 0}
          </div>
          <div className="text-white/60">Ortalama Dinleme</div>
        </div>
      </div>

      {/* Şarkı Listesi */}
      <div className="space-y-4">
        <h2 className="text-2xl font-bold text-white">Şarkılar</h2>
        
        {songs.length === 0 ? (
          <div className="text-center py-12">
            <div className="text-white/60 text-lg">Henüz şarkı yüklenmemiş</div>
            <p className="text-white/40 mt-2">Admin panelinden şarkı ekleyebilirsiniz</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {songs.map((song) => (
              <SongCard
                key={song.id}
                song={song}
                onPlay={() => onPlaySong(song)}
                isCurrentSong={currentSong?.id === song.id}
                isPlaying={isPlaying && currentSong?.id === song.id}
              />
            ))}
          </div>
        )}
      </div>

      {/* Hakkında Bölümü */}
      {content.about && (
        <div className="bg-white/10 backdrop-blur-md rounded-lg p-8">
          <h2 className="text-2xl font-bold text-white mb-4">Hakkında</h2>
          <p className="text-white/80 leading-relaxed">{content.about}</p>
        </div>
      )}

      {/* İletişim */}
      {content.contact && (
        <div className="bg-white/10 backdrop-blur-md rounded-lg p-8 text-center">
          <h2 className="text-2xl font-bold text-white mb-4">İletişim</h2>
          <p className="text-white/80">{content.contact}</p>
        </div>
      )}
    </div>
  );
};

const SongCard = ({ song, onPlay, isCurrentSong, isPlaying }) => {
  return (
    <div className="bg-white/10 backdrop-blur-md rounded-lg p-6 hover:bg-white/20 transition-all duration-300 group">
      <div className="flex items-start justify-between mb-4">
        <div className="flex-1">
          <h3 className="text-lg font-semibold text-white truncate">{song.title}</h3>
          <p className="text-white/60 text-sm">{song.artist}</p>
        </div>
        <button
          onClick={onPlay}
          className={`w-10 h-10 rounded-full flex items-center justify-center transition-all duration-300 ${
            isCurrentSong && isPlaying
              ? 'bg-red-500 hover:bg-red-600'
              : 'bg-white/20 hover:bg-white/30'
          }`}
        >
          {isCurrentSong && isPlaying ? (
            <div className="w-4 h-4 bg-white rounded-sm"></div>
          ) : (
            <Play className="w-4 h-4 text-white ml-1" />
          )}
        </button>
      </div>

      {song.description && (
        <p className="text-white/60 text-sm mb-4 line-clamp-2">{song.description}</p>
      )}

      <div className="flex items-center justify-between text-sm text-white/40">
        <div className="flex items-center space-x-4">
          <div className="flex items-center space-x-1">
            <Eye className="w-4 h-4" />
            <span>{song.plays || 0}</span>
          </div>
          <div className="flex items-center space-x-1">
            <Heart className="w-4 h-4" />
            <span>0</span>
          </div>
        </div>
        <span>{new Date(song.uploadDate).toLocaleDateString('tr-TR')}</span>
      </div>
    </div>
  );
};

export default Home;
