import React, { useState } from 'react';

const ZDefteri = () => {
  const [messages, setMessages] = useState([
    { name: 'Ziyaretçi', text: 'Harika bir site olmuş!' },
    { name: 'MC Demo', text: 'Şarkılar çok iyi, başarılar!' },
  ]);
  const [form, setForm] = useState({ name: '', text: '' });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (form.name && form.text) {
      setMessages([{ name: form.name, text: form.text }, ...messages]);
      setForm({ name: '', text: '' });
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center pt-24 pr-60" style={{ background: 'repeating-linear-gradient(0deg, #2d1a1a 0px, #2d1a1a 1px, transparent 1px, transparent 32px), repeating-linear-gradient(90deg, #2d1a1a 0px, #2d1a1a 1px, transparent 1px, transparent 32px)', backgroundColor: '#1a0d0d' }}>
      <div className="w-full max-w-2xl bg-black/70 rounded-2xl p-8 shadow-2xl border-4 border-yellow-900">
        <h2 className="text-4xl font-extrabold text-yellow-300 mb-4" style={{ fontFamily: 'cursive', textShadow: '2px 2px 8px #000' }}>Ziyaretçi Defteri</h2>
        <form onSubmit={handleSubmit} className="mb-6 flex flex-col space-y-2">
          <input type="text" placeholder="Adınız" value={form.name} onChange={e => setForm({ ...form, name: e.target.value })} className="rounded px-3 py-2 bg-yellow-900/30 text-yellow-100 placeholder-yellow-400 focus:outline-none" />
          <textarea placeholder="Mesajınız" value={form.text} onChange={e => setForm({ ...form, text: e.target.value })} className="rounded px-3 py-2 bg-yellow-900/30 text-yellow-100 placeholder-yellow-400 focus:outline-none" rows={3} />
          <button type="submit" className="bg-yellow-700 text-white font-bold px-4 py-2 rounded hover:bg-yellow-800 transition">Gönder</button>
        </form>
        <div className="space-y-2">
          {messages.map((msg, i) => (
            <div key={i} className="bg-yellow-900/30 rounded px-4 py-2 text-yellow-100">
              <span className="font-bold text-yellow-300">{msg.name}:</span> {msg.text}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ZDefteri;
