import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';

const menuItems = [
  { label: 'ANASAYFA', path: '/' },
  { label: 'HAKKIMDA', path: '/hakkimda' },
  { label: 'MEDYA - MP3', path: '/medya' },
  { label: 'Z. DEFTERİ', path: '/zdefteri' },
  { label: 'İLETİŞİM', path: '/iletisim' },
];

const Navbar = () => {
  const location = useLocation();
  const [open, setOpen] = useState(false);

  return (
    <>
      {/* Üstte sabit başlık ve hamburger */}
      <header className="fixed top-0 left-0 w-full z-30 bg-black/80 border-b-4 border-yellow-900 shadow-xl flex items-center justify-between px-4 md:px-0">
        <div className="text-3xl md:text-5xl font-extrabold text-yellow-300 text-center py-4 tracking-widest w-full md:w-auto" style={{ fontFamily: 'cursive', textShadow: '4px 4px 16px #000' }}>
          ByBrawo
        </div>
        {/* Hamburger buton sadece mobilde */}
        <button
          className="md:hidden text-yellow-300 text-3xl focus:outline-none z-40"
          onClick={() => setOpen(!open)}
          aria-label="Menüyü Aç/Kapat"
        >
          {open ? '✕' : '☰'}
        </button>
      </header>
      {/* Sağda menü (masaüstü) */}
      <aside className="hidden md:flex fixed right-0 top-32 z-20 flex-col items-end pr-6 space-y-4 select-none">
        {menuItems.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className={`w-48 text-right text-lg font-bold tracking-widest py-3 px-6 rounded-l-2xl border-4 border-yellow-700 shadow-lg bg-gradient-to-r from-yellow-900 via-yellow-800 to-yellow-700 text-yellow-200 hover:bg-yellow-600 hover:text-white transition-all duration-200 ${location.pathname === item.path ? 'bg-yellow-700 text-white border-yellow-400' : ''}`}
            style={{ fontFamily: 'cursive', letterSpacing: '2px', textShadow: '2px 2px 8px #000' }}
          >
            {item.label}
          </Link>
        ))}
        {/* Admin butonu */}
        <Link
          to="/admin"
          className={`w-48 text-right text-lg font-bold tracking-widest py-3 px-6 rounded-l-2xl border-4 border-red-700 shadow-lg bg-gradient-to-r from-red-900 via-red-800 to-red-700 text-red-200 hover:bg-red-600 hover:text-white transition-all duration-200 ${location.pathname === '/admin' ? 'bg-red-700 text-white border-red-400' : ''}`}
          style={{ fontFamily: 'cursive', letterSpacing: '2px', textShadow: '2px 2px 8px #000' }}
        >
          ADMIN
        </Link>
      </aside>
      {/* Mobil menü */}
      {open && (
        <nav className="md:hidden fixed top-20 left-0 w-full bg-black/95 z-30 flex flex-col items-center space-y-4 py-8 animate-fade-in">
          {menuItems.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              onClick={() => setOpen(false)}
              className={`w-11/12 text-center text-xl font-bold tracking-widest py-3 rounded-2xl border-4 border-yellow-700 shadow-lg bg-gradient-to-r from-yellow-900 via-yellow-800 to-yellow-700 text-yellow-200 hover:bg-yellow-600 hover:text-white transition-all duration-200 ${location.pathname === item.path ? 'bg-yellow-700 text-white border-yellow-400' : ''}`}
              style={{ fontFamily: 'cursive', letterSpacing: '2px', textShadow: '2px 2px 8px #000' }}
            >
              {item.label}
            </Link>
          ))}
          {/* Admin butonu mobilde */}
          <Link
            to="/admin"
            onClick={() => setOpen(false)}
            className={`w-11/12 text-center text-xl font-bold tracking-widest py-3 rounded-2xl border-4 border-red-700 shadow-lg bg-gradient-to-r from-red-900 via-red-800 to-red-700 text-red-200 hover:bg-red-600 hover:text-white transition-all duration-200 ${location.pathname === '/admin' ? 'bg-red-700 text-white border-red-400' : ''}`}
            style={{ fontFamily: 'cursive', letterSpacing: '2px', textShadow: '2px 2px 8px #000' }}
          >
            ADMIN
          </Link>
        </nav>
      )}
      <style>{`
        @media (max-width: 768px) {
          .animate-fade-in {
            animation: fadeIn 0.2s;
          }
          @keyframes fadeIn {
            from { opacity: 0; transform: translateY(-20px); }
            to { opacity: 1; transform: translateY(0); }
          }
        }
      `}</style>
    </>
  );
};

export default Navbar;
