import React, { useState } from 'react';
import { Upload, Trash2, Edit, Save, X, Music, Settings } from 'lucide-react';

const Admin = ({ songs, content, onAddSong, onDeleteSong, onUpdateContent }) => {
  const [activeTab, setActiveTab] = useState('songs');
  const [showAddForm, setShowAddForm] = useState(false);
  const [editingContent, setEditingContent] = useState(false);

  return (
    <div className="max-w-6xl mx-auto">
      <div className="bg-white/10 backdrop-blur-md rounded-lg p-6">
        <h1 className="text-3xl font-bold text-white mb-6">Admin Paneli</h1>
        
        {/* Tab Navigation */}
        <div className="flex space-x-1 mb-6">
          <button
            onClick={() => setActiveTab('songs')}
            className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-colors ${
              activeTab === 'songs'
                ? 'bg-white/20 text-white'
                : 'text-white/60 hover:text-white'
            }`}
          >
            <Music className="w-4 h-4" />
            <span>Şarkılar</span>
          </button>
          <button
            onClick={() => setActiveTab('content')}
            className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition-colors ${
              activeTab === 'content'
                ? 'bg-white/20 text-white'
                : 'text-white/60 hover:text-white'
            }`}
          >
            <Settings className="w-4 h-4" />
            <span>İçerik Yönetimi</span>
          </button>
        </div>

        {/* Tab Content */}
        {activeTab === 'songs' && (
          <SongsTab
            songs={songs}
            onAddSong={onAddSong}
            onDeleteSong={onDeleteSong}
            showAddForm={showAddForm}
            setShowAddForm={setShowAddForm}
          />
        )}

        {activeTab === 'content' && (
          <ContentTab
            content={content}
            onUpdateContent={onUpdateContent}
            editingContent={editingContent}
            setEditingContent={setEditingContent}
          />
        )}
      </div>
    </div>
  );
};

const SongsTab = ({ songs, onAddSong, onDeleteSong, showAddForm, setShowAddForm }) => {
  const [formData, setFormData] = useState({
    title: '',
    artist: '',
    description: '',
    file: null
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.file) {
      onAddSong(formData);
      setFormData({ title: '', artist: '', description: '', file: null });
      setShowAddForm(false);
    }
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, file: e.target.files[0] });
  };

  return (
    <div className="space-y-6">
      {/* Add Song Button */}
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-white">Şarkı Yönetimi</h2>
        <button
          onClick={() => setShowAddForm(!showAddForm)}
          className="flex items-center space-x-2 bg-purple-600 hover:bg-purple-700 text-white px-4 py-2 rounded-lg transition-colors"
        >
          <Upload className="w-4 h-4" />
          <span>Şarkı Ekle</span>
        </button>
      </div>

      {/* Add Song Form */}
      {showAddForm && (
        <div className="bg-white/5 rounded-lg p-6">
          <h3 className="text-lg font-semibold text-white mb-4">Yeni Şarkı Ekle</h3>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label className="block text-white/80 text-sm mb-2">Şarkı Adı</label>
                <input
                  type="text"
                  value={formData.title}
                  onChange={(e) => setFormData({ ...formData, title: e.target.value })}
                  className="w-full bg-white/10 border border-white/20 rounded-lg px-3 py-2 text-white placeholder-white/40 focus:outline-none focus:border-purple-500"
                  placeholder="Şarkı adını girin"
                />
              </div>
              <div>
                <label className="block text-white/80 text-sm mb-2">Sanatçı</label>
                <input
                  type="text"
                  value={formData.artist}
                  onChange={(e) => setFormData({ ...formData, artist: e.target.value })}
                  className="w-full bg-white/10 border border-white/20 rounded-lg px-3 py-2 text-white placeholder-white/40 focus:outline-none focus:border-purple-500"
                  placeholder="Sanatçı adını girin"
                />
              </div>
            </div>
            <div>
              <label className="block text-white/80 text-sm mb-2">Açıklama</label>
              <textarea
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                className="w-full bg-white/10 border border-white/20 rounded-lg px-3 py-2 text-white placeholder-white/40 focus:outline-none focus:border-purple-500"
                rows="3"
                placeholder="Şarkı hakkında açıklama"
              />
            </div>
            <div>
              <label className="block text-white/80 text-sm mb-2">Müzik Dosyası</label>
              <input
                type="file"
                accept="audio/*"
                onChange={handleFileChange}
                className="w-full bg-white/10 border border-white/20 rounded-lg px-3 py-2 text-white file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-purple-600 file:text-white hover:file:bg-purple-700"
              />
            </div>
            <div className="flex space-x-3">
              <button
                type="submit"
                className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg transition-colors"
              >
                Kaydet
              </button>
              <button
                type="button"
                onClick={() => setShowAddForm(false)}
                className="bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-lg transition-colors"
              >
                İptal
              </button>
            </div>
          </form>
        </div>
      )}

      {/* Songs List */}
      <div className="space-y-4">
        <h3 className="text-xl font-semibold text-white">Mevcut Şarkılar</h3>
        {songs.length === 0 ? (
          <div className="text-center py-8 text-white/60">
            Henüz şarkı yüklenmemiş
          </div>
        ) : (
          <div className="space-y-3">
            {songs.map((song) => (
              <div
                key={song.id}
                className="bg-white/5 rounded-lg p-4 flex items-center justify-between"
              >
                <div className="flex-1">
                  <h4 className="text-white font-semibold">{song.title}</h4>
                  <p className="text-white/60 text-sm">{song.artist}</p>
                  <p className="text-white/40 text-xs">
                    {song.plays || 0} dinleme • {new Date(song.uploadDate).toLocaleDateString('tr-TR')}
                  </p>
                </div>
                <button
                  onClick={() => onDeleteSong(song.id)}
                  className="text-red-400 hover:text-red-300 transition-colors"
                >
                  <Trash2 className="w-5 h-5" />
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

const ContentTab = ({ content, onUpdateContent, editingContent, setEditingContent }) => {
  const [formData, setFormData] = useState(content);

  const handleSubmit = (e) => {
    e.preventDefault();
    onUpdateContent(formData);
    setEditingContent(false);
  };

  const handleCancel = () => {
    setFormData(content);
    setEditingContent(false);
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-white">İçerik Yönetimi</h2>
        {!editingContent && (
          <button
            onClick={() => setEditingContent(true)}
            className="flex items-center space-x-2 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-lg transition-colors"
          >
            <Edit className="w-4 h-4" />
            <span>Düzenle</span>
          </button>
        )}
      </div>

      {editingContent ? (
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-white/80 text-sm mb-2">Site Başlığı</label>
            <input
              type="text"
              value={formData.title}
              onChange={(e) => setFormData({ ...formData, title: e.target.value })}
              className="w-full bg-white/10 border border-white/20 rounded-lg px-3 py-2 text-white placeholder-white/40 focus:outline-none focus:border-blue-500"
            />
          </div>
          <div>
            <label className="block text-white/80 text-sm mb-2">Açıklama</label>
            <input
              type="text"
              value={formData.description}
              onChange={(e) => setFormData({ ...formData, description: e.target.value })}
              className="w-full bg-white/10 border border-white/20 rounded-lg px-3 py-2 text-white placeholder-white/40 focus:outline-none focus:border-blue-500"
            />
          </div>
          <div>
            <label className="block text-white/80 text-sm mb-2">Hakkında</label>
            <textarea
              value={formData.about}
              onChange={(e) => setFormData({ ...formData, about: e.target.value })}
              className="w-full bg-white/10 border border-white/20 rounded-lg px-3 py-2 text-white placeholder-white/40 focus:outline-none focus:border-blue-500"
              rows="4"
            />
          </div>
          <div>
            <label className="block text-white/80 text-sm mb-2">İletişim</label>
            <input
              type="text"
              value={formData.contact}
              onChange={(e) => setFormData({ ...formData, contact: e.target.value })}
              className="w-full bg-white/10 border border-white/20 rounded-lg px-3 py-2 text-white placeholder-white/40 focus:outline-none focus:border-blue-500"
            />
          </div>
          <div className="flex space-x-3">
            <button
              type="submit"
              className="flex items-center space-x-2 bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg transition-colors"
            >
              <Save className="w-4 h-4" />
              <span>Kaydet</span>
            </button>
            <button
              type="button"
              onClick={handleCancel}
              className="flex items-center space-x-2 bg-gray-600 hover:bg-gray-700 text-white px-4 py-2 rounded-lg transition-colors"
            >
              <X className="w-4 h-4" />
              <span>İptal</span>
            </button>
          </div>
        </form>
      ) : (
        <div className="space-y-4">
          <div className="bg-white/5 rounded-lg p-4">
            <h3 className="text-white font-semibold mb-2">Site Başlığı</h3>
            <p className="text-white/80">{content.title}</p>
          </div>
          <div className="bg-white/5 rounded-lg p-4">
            <h3 className="text-white font-semibold mb-2">Açıklama</h3>
            <p className="text-white/80">{content.description}</p>
          </div>
          <div className="bg-white/5 rounded-lg p-4">
            <h3 className="text-white font-semibold mb-2">Hakkında</h3>
            <p className="text-white/80">{content.about}</p>
          </div>
          <div className="bg-white/5 rounded-lg p-4">
            <h3 className="text-white font-semibold mb-2">İletişim</h3>
            <p className="text-white/80">{content.contact}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Admin;
