import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import Hakkimda from './pages/Hakkimda';
import Medya from './pages/Medya';
import ZDefteri from './pages/ZDefteri';
import Iletisim from './pages/Iletisim';
import Admin from './pages/Admin';
import MusicPlayer from './components/MusicPlayer';
import axios from 'axios';

function App() {
  const [songs, setSongs] = useState([]);
  const [content, setContent] = useState({});
  const [currentSong, setCurrentSong] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [audio] = useState(new Audio());
  const [messages, setMessages] = useState([
    { name: 'Ziyaretçi', text: 'Harika bir site olmuş!' },
    { name: 'MC Demo', text: 'Şarkılar çok iyi, başarılar!' },
  ]);
  const [theme, setTheme] = useState({ title: 'ByBrawo', color: '#FFD600' });

  useEffect(() => {
    fetchSongs();
    fetchContent();
  }, []);

  const fetchSongs = async () => {
    try {
      const response = await axios.get('/api/songs');
      setSongs(response.data);
    } catch (error) {
      console.error('Şarkılar yüklenirken hata:', error);
    }
  };

  const fetchContent = async () => {
    try {
      const response = await axios.get('/api/content');
      setContent(response.data);
    } catch (error) {
      console.error('İçerik yüklenirken hata:', error);
    }
  };

  const playSong = (song) => {
    if (currentSong && currentSong.id === song.id) {
      if (isPlaying) {
        audio.pause();
        setIsPlaying(false);
      } else {
        audio.play();
        setIsPlaying(true);
      }
    } else {
      audio.src = `http://localhost:5000/uploads/${song.filename}`;
      audio.play();
      setCurrentSong(song);
      setIsPlaying(true);
      axios.post(`/api/songs/${song.id}/play`);
    }
  };

  return (
    <div className="min-h-screen bg-black">
      <Navbar />
      <Routes>
        <Route path="/" element={<Home songs={songs} content={content} onPlaySong={playSong} currentSong={currentSong} isPlaying={isPlaying} />} />
        <Route path="/hakkimda" element={<Hakkimda content={content} />} />
        <Route path="/medya" element={<Medya songs={songs} />} />
        <Route path="/zdefteri" element={<ZDefteri />} />
        <Route path="/iletisim" element={<Iletisim content={content} />} />
        <Route path="/admin" element={<Admin songs={songs} setSongs={setSongs} content={content} setContent={setContent} messages={messages} setMessages={setMessages} theme={theme} setTheme={setTheme} />} />
      </Routes>
      {currentSong && (
        <MusicPlayer
          song={currentSong}
          isPlaying={isPlaying}
          onPlayPause={() => playSong(currentSong)}
          audio={audio}
        />
      )}
    </div>
  );
}

export default App;
