import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './pages/Home';
import MusicPlayer from './components/MusicPlayer';
import axios from 'axios';

function App() {
  const [songs, setSongs] = useState([]);
  const [content, setContent] = useState({});
  const [currentSong, setCurrentSong] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [audio] = useState(new Audio());

  useEffect(() => {
    fetchSongs();
    fetchContent();
  }, []);

  const fetchSongs = async () => {
    try {
      const response = await axios.get('/api/songs');
      setSongs(response.data);
    } catch (error) {
      console.error('Şarkılar yüklenirken hata:', error);
    }
  };

  const fetchContent = async () => {
    try {
      const response = await axios.get('/api/content');
      setContent(response.data);
    } catch (error) {
      console.error('İçerik yüklenirken hata:', error);
    }
  };

  const playSong = (song) => {
    if (currentSong && currentSong.id === song.id) {
      if (isPlaying) {
        audio.pause();
        setIsPlaying(false);
      } else {
        audio.play();
        setIsPlaying(true);
      }
    } else {
      audio.src = `http://localhost:5000/uploads/${song.filename}`;
      audio.play();
      setCurrentSong(song);
      setIsPlaying(true);
      axios.post(`/api/songs/${song.id}/play`);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900">
      <Navbar content={content} />
      <div className="container mx-auto px-4 py-8">
        <Routes>
          <Route 
            path="/" 
            element={
              <Home 
                songs={songs} 
                content={content}
                onPlaySong={playSong}
                currentSong={currentSong}
                isPlaying={isPlaying}
              />
            } 
          />
        </Routes>
      </div>
      {currentSong && (
        <MusicPlayer
          song={currentSong}
          isPlaying={isPlaying}
          onPlayPause={() => playSong(currentSong)}
          audio={audio}
        />
      )}
    </div>
  );
}

export default App;
