import React, { useState } from 'react';
import { Upload, Trash2, Edit, Save, X, Music, Settings } from 'lucide-react';
import axios from 'axios';

const DEFAULT_USER = { username: 'admin', password: '1234' };

const Admin = ({ songs, setSongs, onSongsChange, content, setContent, onContentChange, messages, setMessages, onMessagesChange, theme, setTheme, onThemeChange, banner, onBannerChange }) => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [login, setLogin] = useState({ username: '', password: '' });
  const [tab, setTab] = useState('theme');
  const [songForm, setSongForm] = useState({ title: '', artist: '', description: '', file: null });
  const [contentForm, setContentForm] = useState(content);
  const [themeForm, setThemeForm] = useState(theme);
  const [bannerFile, setBannerFile] = useState(null);

  // Giriş kontrolü
  const handleLogin = (e) => {
    e.preventDefault();
    if (login.username === DEFAULT_USER.username && login.password === DEFAULT_USER.password) {
      setLoggedIn(true);
    } else {
      alert('Kullanıcı adı veya şifre yanlış!');
    }
  };

  // Şarkı ekle
  const handleAddSong = (e) => {
    e.preventDefault();
    if (!songForm.title || !songForm.artist || !songForm.file) return;
    const newSong = {
      id: Date.now().toString(),
      title: songForm.title,
      artist: songForm.artist,
      description: songForm.description,
      filename: songForm.file.name,
      uploadDate: new Date().toISOString(),
      plays: 0
    };
    setSongs([...songs, newSong]);
    setSongForm({ title: '', artist: '', description: '', file: null });
  };

  // Şarkı sil
  const handleDeleteSong = (id) => {
    setSongs(songs.filter(s => s.id !== id));
  };

  // İçerik güncelle
  const handleContentSave = (e) => {
    e.preventDefault();
    setContent(contentForm);
  };

  // Tema güncelle
  const handleThemeSave = async (e) => {
    e.preventDefault();
    await onThemeChange(themeForm);
  };

  // Banner yükle
  const handleBannerUpload = async (e) => {
    e.preventDefault();
    if (!bannerFile) return;
    
    const formData = new FormData();
    formData.append('banner', bannerFile);
    formData.append('type', 'banner');
    
    try {
      await axios.post('/api/banner', formData);
      setBannerFile(null);
      onBannerChange();
    } catch (error) {
      console.error('Banner yüklenirken hata:', error);
      alert('Banner yüklenirken hata oluştu!');
    }
  };

  // Banner sil
  const handleBannerDelete = async () => {
    try {
      await axios.delete('/api/banner');
      onBannerChange();
    } catch (error) {
      console.error('Banner silinirken hata:', error);
      alert('Banner silinirken hata oluştu!');
    }
  };

  // Z Defteri yorumu sil (API)
  const handleDeleteMessage = async (id) => {
    await axios.delete(`/api/messages/${id}`);
    onMessagesChange();
  };

  if (!loggedIn) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center bg-black px-2">
        <form onSubmit={handleLogin} className="bg-yellow-900/90 rounded-2xl p-8 shadow-2xl border-4 border-yellow-700 w-full max-w-sm flex flex-col space-y-4">
          <h2 className="text-3xl font-bold text-yellow-200 text-center mb-2">Admin Girişi</h2>
          <input type="text" placeholder="Kullanıcı Adı" value={login.username} onChange={e => setLogin({ ...login, username: e.target.value })} className="rounded px-3 py-3 bg-yellow-800 text-yellow-100 placeholder-yellow-400 focus:outline-none text-lg" />
          <input type="password" placeholder="Şifre" value={login.password} onChange={e => setLogin({ ...login, password: e.target.value })} className="rounded px-3 py-3 bg-yellow-800 text-yellow-100 placeholder-yellow-400 focus:outline-none text-lg" />
          <button type="submit" className="bg-yellow-700 text-white font-bold px-4 py-3 rounded hover:bg-yellow-800 transition text-lg">Giriş Yap</button>
        </form>
      </div>
    );
  }

  return (
    <div className="min-h-screen flex flex-col items-center pt-32 md:pt-24 md:pr-60 px-2 md:px-0 bg-black">
      <div className="w-full max-w-3xl bg-black/80 rounded-2xl p-4 sm:p-8 shadow-2xl border-4 border-theme">
        <div className="flex flex-wrap gap-2 mb-6 justify-center">
          <button onClick={() => setTab('theme')} className={`px-4 py-2 rounded font-bold ${tab==='theme' ? 'bg-theme text-white' : 'bg-yellow-900 text-theme'}`}>Tema</button>
          <button onClick={() => setTab('banner')} className={`px-4 py-2 rounded font-bold ${tab==='banner' ? 'bg-theme text-white' : 'bg-yellow-900 text-theme'}`}>Banner</button>
          <button onClick={() => setTab('songs')} className={`px-4 py-2 rounded font-bold ${tab==='songs' ? 'bg-theme text-white' : 'bg-yellow-900 text-theme'}`}>Şarkılar</button>
          <button onClick={() => setTab('content')} className={`px-4 py-2 rounded font-bold ${tab==='content' ? 'bg-theme text-white' : 'bg-yellow-900 text-theme'}`}>İçerik</button>
          <button onClick={() => setTab('zdefteri')} className={`px-4 py-2 rounded font-bold ${tab==='zdefteri' ? 'bg-theme text-white' : 'bg-yellow-900 text-theme'}`}>Z Defteri</button>
        </div>
        {/* Tema Ayarları */}
        {tab === 'theme' && (
          <form onSubmit={handleThemeSave} className="flex flex-col space-y-4">
            <h3 className="text-2xl font-bold text-theme">Tema Ayarları</h3>
            <label className="text-theme">Başlık
              <input type="text" value={themeForm.title || ''} onChange={e => setThemeForm({ ...themeForm, title: e.target.value })} className="block w-full rounded px-3 py-2 mt-1 bg-yellow-900 text-theme" />
            </label>
            <label className="text-theme">Ana Renk (Primary)
              <input type="color" value={themeForm.primaryColor || '#FFD600'} onChange={e => setThemeForm({ ...themeForm, primaryColor: e.target.value })} className="block w-16 h-10 mt-1" />
            </label>
            <label className="text-theme">İkincil Renk (Secondary)
              <input type="color" value={themeForm.secondaryColor || '#FFA500'} onChange={e => setThemeForm({ ...themeForm, secondaryColor: e.target.value })} className="block w-16 h-10 mt-1" />
            </label>
            <label className="text-theme">Vurgu Rengi (Accent)
              <input type="color" value={themeForm.accentColor || '#FF6B35'} onChange={e => setThemeForm({ ...themeForm, accentColor: e.target.value })} className="block w-16 h-10 mt-1" />
            </label>
            <label className="text-theme">Arka Plan Rengi
              <input type="color" value={themeForm.backgroundColor || '#1a0d0d'} onChange={e => setThemeForm({ ...themeForm, backgroundColor: e.target.value })} className="block w-16 h-10 mt-1" />
            </label>
            <label className="text-theme">Metin Rengi
              <input type="color" value={themeForm.textColor || '#FFFFFF'} onChange={e => setThemeForm({ ...themeForm, textColor: e.target.value })} className="block w-16 h-10 mt-1" />
            </label>
            <button type="submit" className="bg-theme text-white font-bold px-4 py-2 rounded hover:bg-yellow-800 transition">Kaydet</button>
          </form>
        )}
        {/* Banner Yönetimi */}
        {tab === 'banner' && (
          <div className="flex flex-col space-y-4">
            <h3 className="text-2xl font-bold text-theme">Banner Yönetimi</h3>
            {banner ? (
              <div className="space-y-4">
                <div className="bg-yellow-900/30 rounded p-4">
                  <img src={`http://localhost:5000/uploads/banners/${banner.filename}`} alt="Banner" className="w-full max-w-md mx-auto rounded" />
                  <p className="text-theme mt-2 text-center">{banner.originalName}</p>
                </div>
                <button onClick={handleBannerDelete} className="bg-red-700 text-white font-bold px-4 py-2 rounded hover:bg-red-800 transition">Banner'ı Sil</button>
              </div>
            ) : (
              <div className="text-theme text-center py-8">Henüz banner yüklenmemiş</div>
            )}
            <form onSubmit={handleBannerUpload} className="flex flex-col space-y-2">
              <input type="file" accept="image/*" onChange={e => setBannerFile(e.target.files[0])} className="rounded px-3 py-2 bg-yellow-900 text-theme" />
              <button type="submit" disabled={!bannerFile} className="bg-theme text-white font-bold px-4 py-2 rounded hover:bg-yellow-800 transition disabled:opacity-50">Banner Yükle</button>
            </form>
          </div>
        )}
        {/* Şarkı Yönetimi */}
        {tab === 'songs' && (
          <div>
            <h3 className="text-2xl font-bold text-theme mb-4">Şarkı Yönetimi</h3>
            <form onSubmit={handleAddSong} className="flex flex-col space-y-2 mb-6">
              <input type="text" placeholder="Şarkı Adı" value={songForm.title} onChange={e => setSongForm({ ...songForm, title: e.target.value })} className="rounded px-3 py-2 bg-yellow-900 text-theme" />
              <input type="text" placeholder="Sanatçı" value={songForm.artist} onChange={e => setSongForm({ ...songForm, artist: e.target.value })} className="rounded px-3 py-2 bg-yellow-900 text-theme" />
              <input type="text" placeholder="Açıklama" value={songForm.description} onChange={e => setSongForm({ ...songForm, description: e.target.value })} className="rounded px-3 py-2 bg-yellow-900 text-theme" />
              <input type="file" accept="audio/*" onChange={e => setSongForm({ ...songForm, file: e.target.files[0] })} className="rounded px-3 py-2 bg-yellow-900 text-theme" />
              <button type="submit" className="bg-theme text-white font-bold px-4 py-2 rounded hover:bg-yellow-800 transition">Şarkı Ekle</button>
            </form>
            <ul className="space-y-2">
              {songs.map((song, i) => (
                <li key={song.id} className="flex flex-col sm:flex-row items-start sm:items-center justify-between px-2 sm:px-4 py-2 rounded-lg bg-yellow-900/30">
                  <span className="text-theme font-semibold text-base sm:text-lg">{String(i+1).padStart(2, '0')}. {song.title} <span className="text-theme-400">{song.artist && `- ${song.artist}`}</span></span>
                  <button onClick={() => handleDeleteSong(song.id)} className="mt-2 sm:mt-0 sm:ml-4 px-4 py-2 rounded bg-red-700 text-white font-bold shadow text-base sm:text-lg hover:bg-red-800">Sil</button>
                </li>
              ))}
            </ul>
          </div>
        )}
        {/* İçerik Yönetimi */}
        {tab === 'content' && (
          <form onSubmit={handleContentSave} className="flex flex-col space-y-4">
            <h3 className="text-2xl font-bold text-theme">İçerik Yönetimi</h3>
            <label className="text-theme">Başlık
              <input type="text" value={contentForm.title || ''} onChange={e => setContentForm({ ...contentForm, title: e.target.value })} className="block w-full rounded px-3 py-2 mt-1 bg-yellow-900 text-theme" />
            </label>
            <label className="text-theme">Açıklama
              <input type="text" value={contentForm.description || ''} onChange={e => setContentForm({ ...contentForm, description: e.target.value })} className="block w-full rounded px-3 py-2 mt-1 bg-yellow-900 text-theme" />
            </label>
            <label className="text-theme">Hakkında
              <textarea value={contentForm.about || ''} onChange={e => setContentForm({ ...contentForm, about: e.target.value })} className="block w-full rounded px-3 py-2 mt-1 bg-yellow-900 text-theme" rows={3} />
            </label>
            <label className="text-theme">İletişim
              <input type="text" value={contentForm.contact || ''} onChange={e => setContentForm({ ...contentForm, contact: e.target.value })} className="block w-full rounded px-3 py-2 mt-1 bg-yellow-900 text-theme" />
            </label>
            <button type="submit" className="bg-theme text-white font-bold px-4 py-2 rounded hover:bg-yellow-800 transition">Kaydet</button>
          </form>
        )}
        {/* Z Defteri Yönetimi */}
        {tab === 'zdefteri' && (
          <div>
            <h3 className="text-2xl font-bold text-theme mb-4">Ziyaretçi Defteri</h3>
            <ul className="space-y-2">
              {messages.map((msg) => (
                <li key={msg.id} className="flex flex-col sm:flex-row items-start sm:items-center justify-between px-2 sm:px-4 py-2 rounded-lg bg-yellow-900/30">
                  <span className="text-theme text-base sm:text-lg"><span className="font-bold text-theme">{msg.name}:</span> {msg.text}</span>
                  <button onClick={() => handleDeleteMessage(msg.id)} className="mt-2 sm:mt-0 sm:ml-4 px-4 py-2 rounded bg-red-700 text-white font-bold shadow text-base sm:text-lg hover:bg-red-800">Sil</button>
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default Admin;
